mapper.delete:
DELETE FROM user_like
WHERE user_number = #{user_number}
  AND data_id = #{data_id}

mapper.select:
SELECT IF(COUNT(*) > 0, true, false)
FROM user_like
WHERE user_number = #{user_number}
  AND data_id = #{data_id}

mapper.insert:
INSERT INTO user_pingfen
(user_number,
 data_id,
 count_score
)
VALUES
    (#{user_number},
     #{data_id},
     #{count_score}
    )

mapper.update:
UPDATE mydata
SET
    count_people = count_people + 1,
    count_score = count_score + #{count_score}
WHERE id = #{data_id}

mapper.select:
SELECT * FROM user_pingfen
WHERE user_number = #{user_number}
  AND data_id = #{data_id}

mapper.delete:
