WHERE user_number = #{user_number}
  AND data_id = #{data_id}

mapper.select:
SELECT IF(COUNT(*) > 0, true, false)
FROM user_collection
WHERE user_number = #{user_number}
  AND data_id = #{data_id}

mapper.delete:
